		ELSE  
			SELECT -1 AS "Codigo", 'El Cuestionario No Puede Ser Respondido Por El Usuario En Este Momento' AS "Mensaje";
		END IF;
	ELSE  
		SELECT -1 AS "Codigo", 'La Pregunta No Se Encuentra Registrada' AS "Mensaje";
