            SELECT @IdUsuario AS "Codigo",  @TipoUsuario AS "Mensaje";
        ELSE
            SELECT -1 AS "Codigo", 'El Usuario No Se Encuentra Registrado' AS "Mensaje";
        END IF;
        
    END IF;
END ;;
