	IN _IdCurso INT,
    IN _IdUsuario INT,
    IN _IdPregunta INT,
    IN _Respuesta VARCHAR(15)
)
BEGIN
	

	IF EXISTS(SELECT IdPregunta FROM tb_preguntascuestionarios WHERE IdPregunta = _IdPregunta) THEN

		IF EXISTS(SELECT CursosUsuarios.IdCurso FROM 
			tb_cursosusuarios CursosUsuarios
