BEGIN

	SELECT Chats.IdChat, courseroom.fn_IDUsuarioDiferenteChat(Chats.IdChat,_IdUsuario) AS IdUsuario,
    courseroom.fn_NombreUsuarioDiferenteChat(Chats.IdChat,_IdUsuario) AS NombreCompleto,
