)
BEGIN

    IF EXISTS(SELECT IdGrupo FROM tb_grupos WHERE IdGrupo = _IdGrupo AND Activo = 1) THEN

        IF courseroom.fn_ExisteUsuario(_IdUsuario) = 1 THEN

            IF courseroom.fn_IntentosAceptablesGrupo(_IdGrupo, _IdUsuario) = 1 THEN
                
