    -- Validar que exista el curso:
    IF EXISTS (SELECT IdCurso FROM tb_cursos WHERE IdCurso = _IdCurso AND Activo = 1 AND Finalizado = 0 AND IdProfesor = _IdProfesor) THEN
       
