				SELECT courseroom.fn_AgregarAvisoPregunta(_IdPregunta, _IdUsuarioEmisor, CONCAT('Tiene Un Nuevo Mensaje En La Pregunta ',fn_NombrePregunta(_IdPregunta)), 'Pregunta') INTO @IdUsuario;

                SELECT @Id AS "Id", @IdUsuario AS "Codigo", 'Mensaje Agregado Satisfactoriamente' AS "Mensaje";

            ELSE
                SELECT -1 AS "Id",-1 AS "Codigo", 'El Usuario Emisor No Se Encuentra Registrado' AS "Mensaje";
            END IF;

        ELSE
            SELECT -1 AS "Id",-1 AS "Codigo", 'La Pregunta No Se Encuentra Registrada' AS "Mensaje";
        END IF;

    ELSE
        SELECT -1 AS "Id",-1 AS "Codigo", 'Algún Parámetro De Entrada No Cuenta Con El Formato Adecuado' AS "Mensaje";
    END IF;

END ;;
