        INNER JOIN tb_chatspersonales Chats ON Chats.IdUsuarioReceptor = Usuarios.IdUsuario
        WHERE Chats.IdChat = _IdChat AND Chats.Activo = 1 LIMIT 1;
    END IF;
END ;;
